interface Process { name: string; pid: number parentPid: number uid: number gid: number executePath: string command: string virtualMemoryUsage: number // In KB physicalMemoryUsage: number // In KB cpuTime: number // In ms cpuUsage: number // In % networkInBandwidth: number // What interface ??? networkOutBandwidth: number ioWrite: number // In KB ioRead: number // In KB
} interface NetworkInterface { name: string inBandwidth: number outBandwidth: number
} interface Memory { used: number available: number swapUsed?: number swapFree?: number
} interface Cpu { user: number nice: number system: number iowait: number steal: number idle: number
} interface IOUsage { deviceName: string readPerSecond: number writePerSecond: number
} interface DiskUsage { filesystemName: string used: number // In KB available: number // In KB
}
